import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { RegisterRequest } from '../../shared/models/auth.models';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  registerForm: FormGroup;
  loading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.registerForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      displayName: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.registerForm.valid) {
      this.loading = true;
      this.error = null;

      const registerRequest: RegisterRequest = this.registerForm.value;

      this.authService.register(registerRequest).subscribe({
        next: (response) => {
          this.loading = false;
          // Auto-login after successful registration
          this.authService.login({
            email: registerRequest.email,
            password: registerRequest.password
          }).subscribe({
            next: () => {
              this.router.navigate(['/rooms']);
            },
            error: () => {
              this.router.navigate(['/login']);
            }
          });
        },
        error: (error) => {
          this.loading = false;
          this.error = 'Erreur lors de l\'inscription. Vérifiez vos informations.';
          console.error('Register error:', error);
        }
      });
    }
  }

  navigateToLogin(): void {
    this.router.navigate(['/login']);
  }
}
<div class="register-container">
  <div class="register-card">
    <h2>Inscription StudyBuddy</h2>
    
    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="displayName">Nom d'affichage:</label>
        <input 
          type="text" 
          id="displayName" 
          formControlName="displayName" 
          class="form-control"
          [class.is-invalid]="registerForm.get('displayName')?.invalid && registerForm.get('displayName')?.touched">
      </div>

      <div class="form-group">
        <label for="email">Email:</label>
        <input 
          type="email" 
          id="email" 
          formControlName="email" 
          class="form-control"
          [class.is-invalid]="registerForm.get('email')?.invalid && registerForm.get('email')?.touched">
      </div>
      
      <div class="form-group">
        <label for="password">Mot de passe:</label>
        <input 
          type="password" 
          id="password" 
          formControlName="password" 
          class="form-control"
          [class.is-invalid]="registerForm.get('password')?.invalid && registerForm.get('password')?.touched">
        <small class="form-text">Minimum 6 caractères</small>
      </div>
      
      <div class="error-message" *ngIf="error">
        {{ error }}
      </div>
      
      <button 
        type="submit" 
        class="btn btn-primary" 
        [disabled]="registerForm.invalid || loading">
        {{ loading ? 'Inscription...' : 'S\'inscrire' }}
      </button>
    </form>
    
    <p class="login-link">
      Déjà inscrit ? 
      <a href="#" (click)="navigateToLogin()">Connectez-vous</a>
    </p>
  </div>
</div>
