import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { LoginRequest } from '../../shared/models/auth.models';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;
  loading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  onSubmit(): void {
    if (this.loginForm.valid) {
      this.loading = true;
      this.error = null;

      const loginRequest: LoginRequest = this.loginForm.value;

      this.authService.login(loginRequest).subscribe({
        next: (response) => {
          this.loading = false;
          this.router.navigate(['/rooms']);
        },
        error: (error) => {
          this.loading = false;
          this.error = 'Invalid email or password';
          console.error('Login error:', error);
        }
      });
    }
  }

  navigateToRegister(): void {
    this.router.navigate(['/auth/register']);
  }

  // M√©thode de test temporaire pour d√©boguer l'API
  testAuthAPI(): void {
    console.log('üß™ Testing auth API directly...');
    
    const testCredentials = {
      email: this.loginForm.value.email || 'test@example.com',
      password: this.loginForm.value.password || 'password123'
    };
    
    console.log('üì§ Sending test request with:', testCredentials);
    
    fetch('http://localhost:8080/api/auth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(testCredentials)
    })
    .then(response => {
      console.log('üì• Response status:', response.status);
      console.log('üì• Response headers:', response.headers);
      return response.json();
    })
    .then(data => {
      console.log('üîç Raw API response:', data);
      console.log('üîë Token in response:', data.token);
      console.log('üÜî ID in response:', data.id);
      console.log('üìã Response keys:', Object.keys(data));
    })
    .catch(error => {
      console.error('‚ùå Test API error:', error);
    });
  }
}
<div class="login-container">
  <div class="login-card">
    <h2>Connexion StudyBuddy</h2>
    
    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="email">Email:</label>
        <input 
          type="email" 
          id="email" 
          formControlName="email" 
          class="form-control"
          [class.is-invalid]="loginForm.get('email')?.invalid && loginForm.get('email')?.touched">
      </div>
      
      <div class="form-group">
        <label for="password">Mot de passe:</label>
        <input 
          type="password" 
          id="password" 
          formControlName="password" 
          class="form-control"
          [class.is-invalid]="loginForm.get('password')?.invalid && loginForm.get('password')?.touched">
      </div>
      
      <div class="error-message" *ngIf="error">
        {{ error }}
      </div>
      
      <button 
        type="submit" 
        class="btn btn-primary" 
        [disabled]="loginForm.invalid || loading">
        {{ loading ? 'Connexion...' : 'Se connecter' }}
      </button>
    </form>
    
    <p class="register-link">
      Pas encore de compte ? 
      <a routerLink="/auth/register">Inscrivez-vous</a>
    </p>
  </div>
</div>
