import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../auth/auth.service';
import { RoomsService } from '../rooms.service';
import { Room } from '../../shared/models/room.models';

@Component({
  selector: 'app-rooms-list',
  templateUrl: './rooms-list.component.html',
  styleUrls: ['./rooms-list.component.css']
})
export class RoomsListComponent implements OnInit {
  rooms: Room[] = [];
  loading = false;
  showCreateDialog = false;
  createRoomForm: FormGroup;

  constructor(
    private authService: AuthService,
    private roomsService: RoomsService,
    private router: Router,
    private fb: FormBuilder
  ) {
    this.createRoomForm = this.fb.group({
      subject: ['', Validators.required],
      level: ['', Validators.required],
      topic: [''],
      institution: [''],
      focusDuration: [25, [Validators.required, Validators.min(1)]],
      breakDuration: [5, [Validators.required, Validators.min(1)]]
    });
  }

  ngOnInit(): void {
    this.loadRooms();
  }

  loadRooms(): void {
    this.loading = true;
    this.roomsService.getRooms().subscribe({
      next: (rooms) => {
        this.rooms = rooms;
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading rooms:', error);
        this.loading = false;
      }
    });
  }

  openCreateRoomDialog(): void {
    this.showCreateDialog = true;
  }

  hideCreateDialog(): void {
    this.showCreateDialog = false;
    this.createRoomForm.reset({
      focusDuration: 25,
      breakDuration: 5
    });
  }

  onCreateRoom(): void {
    if (this.createRoomForm.valid) {
      const newRoom: Room = {
        ...this.createRoomForm.value,
        isActive: true,
        timerRunning: false,
        isOnBreak: false
      };
      
      console.log('Creating room:', newRoom);
      
      // Call the backend API to create the room
      this.roomsService.createRoom(newRoom).subscribe({
        next: (roomId) => {
          console.log('✅ Room created successfully with ID:', roomId);
          // Add the created room to the local list with the returned ID
          const createdRoom: Room = { ...newRoom, id: roomId };
          this.rooms.push(createdRoom);
          this.hideCreateDialog();
          // Optionally reload the rooms list to get fresh data
          // this.loadRooms();
        },
        error: (error) => {
          console.error('❌ Error creating room:', error);
          // TODO: Show user-friendly error message (toast/snackbar)
          if (error.status === 400) {
            console.error('Invalid room data provided');
          } else if (error.status === 401) {
            console.error('User not authenticated');
          } else {
            console.error('Failed to create room. Please try again.');
          }
        }
      });
    }
  }

  joinRoom(roomId: string): void {
    const userId = this.authService.getCurrentUserId();
    if (!userId) {
      console.error('User not authenticated');
      return;
    }

    console.log(`Attempting to join room ${roomId} with user ${userId}`);
    
    this.roomsService.joinRoom(roomId, userId).subscribe({
      next: () => {
        console.log(`Successfully joined room ${roomId}`);
        this.router.navigate(['/room', roomId]);
      },
      error: (error) => {
        console.error('Error joining room:', error);
        // TODO: Show user-friendly error message (toast/snackbar)
        if (error.status === 404) {
          console.error('Room not found');
        } else if (error.status === 403) {
          console.error('Access denied to room');
        } else {
          console.error('Failed to join room. Please try again.');
        }
      }
    });
  }

  logout(): void {
    this.authService.logout(); // Navigation is handled by the service
  }
}
<div class="rooms-container">
  <header class="rooms-header">
    <h1>Salles d'étude</h1>
    <div class="header-actions">
      <button class="btn btn-primary" (click)="openCreateRoomDialog()">
        Créer une salle
      </button>
      <button class="btn btn-secondary" (click)="logout()">
        Déconnexion
      </button>
    </div>
  </header>

  <div class="loading" *ngIf="loading">
    <p>Chargement des salles...</p>
  </div>

  <div class="rooms-grid" *ngIf="!loading">
    <div class="room-card" *ngFor="let room of rooms">
      <div class="room-info">
        <h3>{{ room.subject }}</h3>
        <p><strong>Niveau:</strong> {{ room.level }}</p>
        <p><strong>Sujet:</strong> {{ room.topic }}</p>
        <p><strong>Focus:</strong> {{ room.focusDuration }} min</p>
        <p><strong>Pause:</strong> {{ room.breakDuration }} min</p>
        <div class="room-status">
          <span class="status" [class.active]="room.isActive">
            {{ room.isActive ? 'Active' : 'Inactive' }}
          </span>
          <span class="timer-status" *ngIf="room.timerRunning">
            {{ room.isOnBreak ? 'En pause' : 'Focus en cours' }}
          </span>
        </div>
      </div>
      <div class="room-actions">
        <button class="btn btn-primary" (click)="joinRoom(room.id!)">
          Rejoindre la salle
        </button>
      </div>
    </div>

    <div class="empty-state" *ngIf="rooms.length === 0">
      <p>Aucune salle disponible. Créez votre première salle d'étude !</p>
    </div>
  </div>

  <!-- Create Room Dialog (inline) -->
  <div class="dialog-overlay" *ngIf="showCreateDialog" (click)="hideCreateDialog()">
    <div class="dialog-content" (click)="$event.stopPropagation()">
      <div class="dialog-header">
        <h2>Créer une nouvelle salle d'étude</h2>
        <button class="close-btn" (click)="hideCreateDialog()">&times;</button>
      </div>
      
      <form [formGroup]="createRoomForm" (ngSubmit)="onCreateRoom()">
        <div class="form-grid">
          <div class="form-group">
            <label for="subject">Matière *</label>
            <input 
              type="text" 
              id="subject" 
              formControlName="subject" 
              class="form-control"
              placeholder="Ex: Mathématiques">
          </div>

          <div class="form-group">
            <label for="level">Niveau *</label>
            <select 
              id="level" 
              formControlName="level" 
              class="form-control">
              <option value="">Sélectionner un niveau</option>
              <option value="Débutant">Débutant</option>
              <option value="Intermédiaire">Intermédiaire</option>
              <option value="Avancé">Avancé</option>
            </select>
          </div>

          <div class="form-group">
            <label for="topic">Sujet</label>
            <input 
              type="text" 
              id="topic" 
              formControlName="topic" 
              class="form-control"
              placeholder="Ex: Calcul différentiel">
          </div>

          <div class="form-group">
            <label for="institution">Institution</label>
            <input 
              type="text" 
              id="institution" 
              formControlName="institution" 
              class="form-control"
              placeholder="Ex: Université Paris">
          </div>

          <div class="form-group">
            <label for="focusDuration">Durée focus (min) *</label>
            <input 
              type="number" 
              id="focusDuration" 
              formControlName="focusDuration" 
              class="form-control"
              min="1"
              max="120">
          </div>

          <div class="form-group">
            <label for="breakDuration">Durée pause (min) *</label>
            <input 
              type="number" 
              id="breakDuration" 
              formControlName="breakDuration" 
              class="form-control"
              min="1"
              max="60">
          </div>
        </div>

        <div class="dialog-actions">
          <button type="button" class="btn btn-secondary" (click)="hideCreateDialog()">
            Annuler
          </button>
          <button type="submit" class="btn btn-primary" [disabled]="createRoomForm.invalid">
            Créer la salle
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
