import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of } from 'rxjs';
import { delay } from 'rxjs/operators';
import { environment } from '../../environments/environment';
import { Room, RoomDurationUpdateRequest } from '../shared/models/room.models';

@Injectable({
  providedIn: 'root'
})
export class RoomsService {
  private baseUrl = environment.apiUrl;

  constructor(private http: HttpClient) {}

  createRoom(room: Room): Observable<string> {
    return this.http.post(`${this.baseUrl}/rooms`, room, { responseType: 'text' });
  }

  getRooms(): Observable<Room[]> {
    // ✅ Real API call - backend now provides GET /api/rooms/getAllRooms
    return this.http.get<Room[]>(`${this.baseUrl}/rooms/getAllRooms`);
    
    // TODO: Remove this mock data block once tested
    // Enhanced mock data matching backend Room model exactly
