import { Component, OnInit, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../auth/auth.service';
import { RoomsService } from '../rooms.service';
import { Room } from '../../shared/models/room.models';
import { CreateRoomDialogComponent } from '../create-room-dialog/create-room-dialog.component';

@Component({
  selector: 'app-rooms-list',
  templateUrl: './rooms-list.component.html',
  styleUrls: ['./rooms-list.component.css']
})
export class RoomsListComponent implements OnInit {
  @ViewChild(CreateRoomDialogComponent) createRoomDialog!: CreateRoomDialogComponent;
  
  rooms: Room[] = [];
  loading = false;

  constructor(
    private authService: AuthService,
    private roomsService: RoomsService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadRooms();
  }

  loadRooms(): void {
    // TODO: Implement when backend provides GET /rooms endpoint
    this.loading = true;
    // Mock data for now
    setTimeout(() => {
      this.rooms = [
        {
          id: '1',
          subject: 'Mathématiques',
          level: 'Intermédiaire',
          topic: 'Calcul différentiel',
          focusDuration: 25,
          breakDuration: 5,
          isActive: true,
          timerRunning: false,
          isOnBreak: false
        },
        {
          id: '2',
          subject: 'Physique',
          level: 'Avancé',
          topic: 'Mécanique quantique',
          focusDuration: 30,
          breakDuration: 10,
          isActive: true,
          timerRunning: true,
          isOnBreak: false
        }
      ];
      this.loading = false;
    }, 1000);
  }

  openCreateRoomDialog(): void {
    this.createRoomDialog.show();
  }

  onRoomCreated(room: Room): void {
    // TODO: Call roomsService.createRoom(room) when backend is ready
    console.log('Room created:', room);
    
    // For now, just add to local list with mock ID
    const newRoom = { ...room, id: (this.rooms.length + 1).toString() };
    this.rooms.push(newRoom);
  }

  joinRoom(roomId: string): void {
    const userId = this.authService.getCurrentUserId();
    if (userId) {
      this.roomsService.joinRoom(roomId, userId).subscribe({
        next: () => {
          this.router.navigate(['/room', roomId]);
        },
        error: (error) => {
          console.error('Error joining room:', error);
        }
      });
    }
  }

  logout(): void {
    this.authService.logout(); // Navigation is handled by the service
  }
}
<div class="rooms-container">
  <header class="rooms-header">
    <h1>Salles d'étude</h1>
    <div class="header-actions">
      <button class="btn btn-primary" (click)="openCreateRoomDialog()">
        Créer une salle
      </button>
      <button class="btn btn-secondary" (click)="logout()">
        Déconnexion
      </button>
    </div>
  </header>

  <div class="loading" *ngIf="loading">
    <p>Chargement des salles...</p>
  </div>

  <div class="rooms-grid" *ngIf="!loading">
    <div class="room-card" *ngFor="let room of rooms">
      <div class="room-info">
        <h3>{{ room.subject }}</h3>
        <p><strong>Niveau:</strong> {{ room.level }}</p>
        <p><strong>Sujet:</strong> {{ room.topic }}</p>
        <p><strong>Focus:</strong> {{ room.focusDuration }} min</p>
        <p><strong>Pause:</strong> {{ room.breakDuration }} min</p>
        <div class="room-status">
          <span class="status" [class.active]="room.isActive">
            {{ room.isActive ? 'Active' : 'Inactive' }}
          </span>
          <span class="timer-status" *ngIf="room.timerRunning">
            {{ room.isOnBreak ? 'En pause' : 'Focus en cours' }}
          </span>
        </div>
      </div>
      <div class="room-actions">
        <button class="btn btn-primary" (click)="joinRoom(room.id!)">
          Rejoindre la salle
        </button>
      </div>
    </div>

    <div class="empty-state" *ngIf="rooms.length === 0">
      <p>Aucune salle disponible. Créez votre première salle d'étude !</p>
    </div>
  </div>
</div>

<!-- Create Room Dialog -->
<app-create-room-dialog 
  (roomCreated)="onRoomCreated($event)"
  (dialogClosed)="loadRooms()">
</app-create-room-dialog>
